		task.dependsOn ':testbed2_api:assembleRelease'
		task.dependsOn ':testbed2_impl:assembleRelease'
		task.dependsOn ':testbed2_android_service:assembleRelease'
		task.dependsOn ':testbed2_android_client:assembleRelease'
		task.dependsOn ':testbed2_android_messenger:assembleRelease'
	}
}

